sys_script_include.script:
var GlobalQualityProjectUtils = Class.create();
GlobalQualityProjectUtils.prototype = {
    initialize: function() {},

    generateXMLFile: function(table,id,query) {
        if (table != "") {
            var request = new sn_ws.RESTMessageV2();
            var instanceURL = gs.getProperty('glide.servlet.uri');
			request.setEndpoint(instanceURL + table + '_list.do?XML&sysparm_query='+ query + table);
            //request.setEndpoint(instanceURL + 'api/now/table/' + table);
            request.setHttpMethod('GET');

            var user = 'api_qp';
            var password = 'api_QP1234';

            request.setBasicAuth(user, password);
            request.setRequestHeader("Accept", "text/xml");
            request.setRequestHeader('Content-Type', 'application/json');
            request.setRequestBody("{}");

            var response = request.execute();
			var time = new GlideDateTime().getDisplayValue();
            var nom_fichier = table + "_" + time + ".xml";
            //Create a Json File to attach to the previous Data Source with the content retrieved from the REST Call
            var attachment = new Attachment();
            var attachmentRec = attachment.write("x_38181_qual_projt_export_qp", id, nom_fichier, "application/json", response.getBody());

			return response.getBody();
        }
    },

	generateXMLFile_other: function(table,id,query) {
        if (table != "") {
            var request = new sn_ws.RESTMessageV2();
            var instanceURL = gs.getProperty('glide.servlet.uri');
			request.setEndpoint(instanceURL + table + '_list.do?XML&sysparm_query='+ query);
            //request.setEndpoint(instanceURL + 'api/now/table/' + table);
            request.setHttpMethod('GET');

            var user = 'api_qp';
            var password = 'api_QP1234';

            request.setBasicAuth(user, password);
            request.setRequestHeader("Accept", "text/xml");
            request.setRequestHeader('Content-Type', 'application/json');
            request.setRequestBody("{}");

            var response = request.execute();
			var time = new GlideDateTime().getDisplayValue();
            var nom_fichier = table + "_" + time + ".xml";
            //Create a Json File to attach to the previous Data Source with the content retrieved from the REST Call
            var attachment = new Attachment();
            var attachmentRec = attachment.write("x_38181_qual_projt_export_qp", id, nom_fichier, "application/json", response.getBody());

			return response.getBody();
        }
    },



    type: 'GlobalQualityProjectUtils'
};
